bool isRunYear(int x){
	if(x < 0){
		x++;
		if(x % 4 == 0) return 1;
	}
	else{
		if(x <= 1582){
			if( x % 4 == 0) return 1;
		}
		else {
			if(x % 4 == 0 && (x % 100 != 0 || x % 400 == 0)) return 1;
		}
	}
	return 0;
}

int yeardays(int x){
	if(x == 1582) return 355;
	return isRunYear(x)?366:365;
}

int monthdays(int year,int mon){
	if(year == 1582 && mon == 10) return a[0][mon] - 10;
	return a[isRunYear(year)][mon];
}

